        this.getCars();
      }

      getCars(){
          axios.get(`http://localhost:8080/api/cars`)
          .then(res => {
          const cars= res.data;
          this.setState({cars})
          })
      }

            deleteRow(id, e){
                console.log(id);

                axios.delete(`http://localhost:8080/api/cars/${id}`,{headers: {
                    'Access-Control-Allow-Origin' : '*',

                }} )
                  .then(res => {
                    console.log(res);
                    console.log(res.data);

                    const cars = this.state.cars.filter(item => item.id !== id);
                    this.setState({ cars });
                    this.getCars();
                  })

        }
