        return (
            <div className="row" style={{padding:10}}>     

{ this.state.cars.map((car => <div className="col-sm-4"key={car._id}style={{padding:10}}>
            <div className="card" style={{}}>
                <div className="card-body">

                    <h5 className="card-title"><span className="badge badge-success">Plaka : {car.plaka}  </span></h5>
                    <h5 className="card-title"><span className="badge badge-info">Marka: {car.marka}</span></h5>
                    <h5 className="card-title"><span className="badge badge-info">Model: {car.model}</span></h5>

                    <button style={{float:'right'}} onClick={(e) => this.deleteRow(car._id, e)} type="button" className="btn btn-danger">Sil</button>
                </div>
            </div>
            </div>))}



            </div>






        )}
}

export default Car
