import axios from 'axios'
import React, { Component } from 'react'


export class Car extends Component {
    constructor(props){
        super(props);

    }
    state={
        cars:[]

    }

   componentDidMount() {
       this.props.getCars();
      }

            deleteRow(id, e){
                console.log(id);
                axios.delete(`http://localhost:8080/api/cars/${id}`, {headers: {
                    "Content-Type": "application/x-www-form-urlencoded",
                  }})
                  .then(res => {
                    console.log(res);
                    console.log(res.data);

                    const cars = this.state.cars.filter(item => item.id !== id);
                    this.setState({ cars });
                  })

        }
    render() {
        return (
            <div className="row" style={{padding:10}}>     

{ this.state.cars.map((car => <div className="col-sm-4"key={car._id}>
            <div className="card" >
                <div className="card-body">

                    <h5 className="card-title"><span className="badge badge-info">Plaka : {car.plaka}  </span></h5>
                    <h5 className="card-title"><span className="badge badge-info">Marka: {car.marka}</span></h5>
                    <h5 className="card-title"><span className="badge badge-info">Model: {car.model}</span></h5>

                    <button style={{float:'right'}} onClick={(e) => this.deleteRow(car._id, e)} type="button" className="btn btn-danger">Sil</button>
                </div>
            </div>
            </div>))}



            </div>






        )}
}

export default Car
